import React, { useState, useEffect } from 'react'
import Dialog from '@mui/material/Dialog'
import "./styles.css"


const AddTaskDialog = ({isOpen, onClose}) => {

    const [taskTitle, setTaskTitle] = useState('')
    const [taskDesc, setTaskDesc] = useState('')
    const [taskUser, setTaskUser] = useState('')
    const [taskStat, setTaskStat] = useState('')
    const [project, setProject] = useState('')
    const [todoDate, setTodoDate] = useState('')

    const onTitleChange = (event) => {
        setTaskTitle(event.target.value)
    }

    const onDescChange = (event) => {
        setTaskDesc(event.target.value)
    }

    const onUserChange = (event) => {
        setTaskUser(event.target.value)
    }

    const setTodoStat = () => {
        setTaskStat('1')
    }
    const setDoingStat = () => {
        setTaskStat('2')
    }
    const setDoneStat = () => {
        setTaskStat('3')
    }
    const onProjectChange = (event) => {
        setProject(event.target.value)
    }
    const onTodoDateChange = (event) => {
        setTodoDate(event.target.value)
    }
    const onCancel = () => {
        setTaskTitle('')
        setTaskDesc('')
        setTaskStat('')
        setProject('')
        setTaskUser('')
        setTodoDate('')
        onClose()
    }
    const onSabmit = () => {
        onClose()
    }


    return (
        <div className="container">
            {
                <Dialog
                    title="Dialog Title"
                    open={isOpen}>

                    <div class="create-new-task">
                    <div class="create-new-task-title">Создание новой задачи</div>
                    <div class="create-new-task-container">
                        <div class="create-new-task-input">Название задачи
                            <input class="create-new-task-fields"
                                onChange={onTitleChange}
                                value={taskTitle}/>
                        </div>
                        <div class="create-new-task-input">Описание задачи
                            <input class="create-new-task-fields" 
                                onChange={onDescChange}
                                value="Введите описание задачи"/>
                        </div>
                        <div class="create-new-task-input">Ответственный за задачу
                            <input class="create-new-task-fields" 
                                onChange={onUserChange}
                                value="Выберите пользователя"/>
                        </div>
                        <div class="create-new-task-input">Статус задачи
                            <div class="create-new-task-tab">
                                <button class="create-new-task-tab-container"
                                    onClick={setTodoStat}
                                    >To-Do
                                </button>
                                <button class="create-new-task-tab-container"
                                    onClick={setDoingStat}
                                    >Doing
                                </button>
                                <button class="create-new-task-tab-container"
                                    onClick={setDoneStat}
                                    >Done
                                </button>
                            </div>
                        </div>
                        <div class="create-new-task-project-deadline">
                            <div class="create-new-task-project-deadline-fields">Проект задачи
                                <input class="create-new-task-project-deadline-input" 
                                    onChange={onProjectChange}
                                    value="Выберите проект"
                                />
                            </div>
                            <div class="create-new-task-project-deadline-fields">Срок выполнения задачи
                                <input class="create-new-task-project-deadline-input" 
                                onChange={onTodoDateChange}
                                value="Введите дату"
                            />
                            </div>
                        </div>
                    </div>
                    <div class="create-new-task-cta">
                        <button class="secondary-button"
                            onClick={onCancel}
                        >
                            Отмена
                        </button>
                        <button class="main-button"
                            onClick={onSabmit}
                        >
                            Создать задачу
                        </button>
                    </div>
                    </div>  
                </Dialog>
            }
        </div>
    );
    
}

export default AddTaskDialog
